  async getDigitalAnamnesis(patientId?: string, doctorId?: string): Promise<DigitalAnamnesis[]> { return []; }
  async createDigitalAnamnesis(anamnesis: UpsertDigitalAnamnesis): Promise<DigitalAnamnesis> { 
