  // Campos comuns
  cpf: text("cpf"), // CPF para brasileiros
  birthDate: timestamp("birth_date"), // Data de nascimento
  gender: text("gender"), // G√™nero
  addressData: text("address_data"), // Endere√ßo completo em JSON

  // Dados m√©dicos (para pacientes) - usando TEXT[] array
  medicalConditions: text("medical_conditions").array(), // Condi√ß√µes m√©dicas atuais
  medications: text("medications").array(), // Medica√ß√µes em uso
  allergies: text("allergies").array(), // Alergias conhecidas

  // Configura√ß√µes de conta
  isActive: integer("is_active").default(1), // 1 = ativo, 0 = inativo
  emailVerified: integer("email_verified").default(0), // 1 = verificado, 0 = n√£o verificado
  termsAccepted: integer("terms_accepted").default(0), // 1 = aceito, 0 = n√£o aceito
  privacyAccepted: integer("privacy_accepted").default(0), // 1 = aceito, 0 = n√£o aceito

  // Dados extras em JSON flex√≠vel
  profileData: text("profile_data"), // JSON string

  // Sistema de sauda√ß√µes de voz
  voiceGreetingsEnabled: integer("voice_greetings_enabled").default(1), // 1 = enabled, 0 = disabled
  lastLoginGreeting: timestamp("last_login_greeting"),

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const scientificStudies = pgTable("scientific_studies", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description"),
  authors: text("authors"), // Autores do estudo
  compound: text("compound"), // CBD, THC, CBG, etc.
  indication: text("indication"), // Epilepsia, Dor cr√¥nica, etc.
  phase: text("phase"), // Fase I, II, III
