  isRead: integer("is_read").default(0),
  createdAt: timestamp("created_at").defaultNow(),
});

// Sistema de Aprendizado Cont√≠nuo - Conversas Armazenadas
export const conversations = pgTable("conversations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  sessionId: text("session_id").notNull(), // ID da sess√£o de conversa
  userId: text("user_id"), // ID do usu√°rio (opcional, pode ser an√¥nimo)
  messages: text("messages").notNull(), // JSON das mensagens da conversa
  context: text("context"), // Contexto detectado (greeting, medical_deep, etc.)
  satisfactionRating: integer("satisfaction_rating"), // 1-5 estrelas
  feedback: text("feedback"), // Feedback do usu√°rio sobre a conversa
  medicalTopics: text("medical_topics"), // Array JSON dos t√≥picos m√©dicos discutidos
  isSuccessful: integer("is_successful").default(0), // 1 se conversa foi bem sucedida
