});

// Base de Conhecimento Din√¢mica - Padr√µes de Aprendizado
export const learningPatterns = pgTable("learning_patterns", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  pattern: text("pattern").notNull(), // Padr√£o detectado (ex: "ansiedade + ins√¥nia")
