  .btn-mobile {
    padding: 1rem 1.25rem;
    font-size: 1rem;
    min-height: 3.5rem;
    border-radius: 12px;
  }

  /* Navigation menu specifically for mobile */
  nav[data-testid="side-navigation"] {
    backdrop-filter: blur(20px);
