                            <div className="mb-4">
                              <TextToSpeech 
                                text={`An√°lise sobre ${mainTab.query}: ${mainTab.response.substring(0, 200).replace(/[üî¨üìäüè•‚ö†Ô∏è]/g, '').replace(/\*\*/g, '')}`}
