import React, { useEffect, useState } from "react";
import { Microscope, Pill, AlertTriangle, Bot, Volume2 } from "lucide-react";
import TextToSpeech from "./TextToSpeech";

interface MainCardProps {
  result: {
    query: string;
    response: string | any;
    meta: {
      counts: {
        studies: number;
        trials: number;
        alerts?: number;
      };
    };
    categories: {
      scientific: Array<{
        id: string;
        title: string;
        description: string;
        compound: string;
        indication: string;
        status: string;
      }>;
      clinical: Array<{
        id: string;
        caseNumber: string;
        description: string;
        indication: string;
        outcome: string;
      }>;
      alerts: Array<{
        id: string;
        type: string;
        message: string;
        priority: string;
        readStatus?: boolean;
      }>;
    };
    crossDataSummary?: string;
  } | null;
  isMinimized?: boolean;
  onToggleMinimize?: () => void;
}

export default function MainCard({ result, isMinimized = false, onToggleMinimize, onCardExpand, onClose }: MainCardProps & { onCardExpand?: (content: string, title: string, autoStartTTS?: boolean) => void; onClose?: () => void }) {
  const [hasAutoPlayed, setHasAutoPlayed] = useState(false);

  // Fun√ß√£o para truncar resposta (8 frases para card principal, 10 para estudos cruzados)
  const truncateToMaxSentences = (text: string, maxSentences: number = 8): string => {
    if (!text) return '';

    // Divide o texto em frases usando pontos, exclama√ß√µes e interroga√ß√µes
    const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);

    // Se tem menos frases que o limite, retorna o texto original
    if (sentences.length <= maxSentences) {
      return text;
    }

    // Pega as primeiras frases e reconstr√≥i o texto
    const truncatedSentences = sentences.slice(0, maxSentences);
    return truncatedSentences.join('. ') + '.';
  };

  // Auto-reprodu√ß√£o da resposta integrada (Plataforma + NOA ESPERAN√áA)
  useEffect(() => {
    if (result && !hasAutoPlayed && !isMinimized) {
      let originalText = '';
      if (typeof result.response === 'string') {
        originalText = result.response;
      } else if (typeof result.response === 'object' && result.response !== null && 'response' in result.response) {
        originalText = String(result.response.response);
      }

      // Resposta integrada para TTS
      const integratedResponse = `${originalText.replace(/[üî¨üìäüè•‚ö†Ô∏èüß†üíäüéØ]/g, '').replace(/\*\*/g, '')}. An√°lise complementar NOA ESPERAN√áA: ${result.crossDataSummary || `Com base nos dados da plataforma, identifico correla√ß√µes espec√≠ficas nos ${result.meta.counts.studies} estudos cient√≠ficos e ${result.meta.counts.trials} casos cl√≠nicos dispon√≠veis.`}`;

      // Reprodu√ß√£o autom√°tica usando TTS - FOR√áAR VOZ FEMININA
      const utterance = new SpeechSynthesisUtterance(integratedResponse);
      utterance.lang = 'pt-BR';
      utterance.rate = 0.85;  // Velocidade mais natural
      utterance.pitch = 1.3;  // Pitch bem feminino (era 1.1 - muito baixo!)
      utterance.volume = 0.9;

      // Priorizar voz feminina Microsoft Maria - BUSCA MAIS AGRESSIVA
      const voices = speechSynthesis.getVoices();
      const femaleVoice = voices.find(voice => 
        voice.lang === 'pt-BR' &&
        (voice.name.includes('Maria') || voice.name.includes('Feminina') || 
         voice.name.includes('female') || voice.name.includes('Luciana') ||
         voice.name.includes('Fiona'))
      ) || voices.find(voice => 
        voice.lang.includes('pt-BR') && !voice.name.includes('male')
      ) || voices.find(voice => voice.lang.includes('pt'));
      if (femaleVoice) utterance.voice = femaleVoice;

      speechSynthesis.speak(utterance);
      setHasAutoPlayed(true);
    }
  }, [result, hasAutoPlayed, isMinimized]);

  if (!result) {
    return (
      <div style={{ 
        height: isMinimized ? "80px" : "400px", 
        borderRadius: 8, 
        padding: "12px 16px", 
        background: "#0f172a", 
        color: "#fff", 
        display: "flex", 
        alignItems: "center", 
        justifyContent: "center",
        transition: "height 0.3s ease",
        position: "relative"
