import { useToast } from '@/hooks/use-toast';
import { UserPlus, Eye, EyeOff, Stethoscope, User } from 'lucide-react';

export default function Register() {
  const [, setLocation] = useLocation();
  const [userType, setUserType] = useState<'common' | 'professional'>('common');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    credentialType: '',
    credentialNumber: '',
    specialty: '',
    workArea: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    // Valida√ß√µes
    if (formData.password !== formData.confirmPassword) {
      toast({
        title: "Erro de valida√ß√£o",
        description: "As senhas n√£o coincidem",
        variant: "destructive",
      });
      setIsLoading(false);
      return;
    }

    if (formData.password.length < 8) {
      toast({
        title: "Erro de valida√ß√£o",
        description: "A senha deve ter pelo menos 8 caracteres",
        variant: "destructive",
      });
      setIsLoading(false);
      return;
    }

    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          password: formData.password,
          userType: userType,
          credentialType: formData.credentialType,
          credentialNumber: formData.credentialNumber,
          specialty: formData.specialty,
          workArea: formData.workArea
        }),
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "Cadastro realizado com sucesso!",
          description: "Sua conta foi criada. Redirecionando para o login...",
        });
        setTimeout(() => {
          setLocation('/login');
        }, 2000);
      } else {
        toast({
          title: "Erro no cadastro",
          description: data.message || "Erro ao criar conta",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Erro no cadastro",
        description: "Erro de conex√£o com o servidor",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-[radial-gradient(ellipse_at_top,_var(--tw-gradient-stops))] from-slate-900 via-green-900 to-slate-900 flex items-center justify-center p-3 sm:p-4">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
