import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Switch } from '@/components/ui/switch';
import { Button } from '@/components/ui/button';
import { Slider } from '@/components/ui/slider';
import { useAuth } from '@/hooks/useAuth';
import { useVoiceGreeting } from '@/hooks/useVoiceGreeting';
// import { VoiceMessagePreview } from './VoiceGreetingIndicator'; // DESABILITADO - sistema antigo removido

export default function VoiceSettings() {
  const { user } = useAuth();
  const { playGreeting, isSupported, currentMessage } = useVoiceGreeting();

  const [voiceEnabled, setVoiceEnabled] = useState(true);
  const [volume, setVolume] = useState(70);
  const [rate, setRate] = useState(90);
  const [pitch, setPitch] = useState(100);
  const [isTestPlaying, setIsTestPlaying] = useState(false);

  // Carregar configura√ß√µes do localStorage
  useEffect(() => {
    if (user) {
      const savedSettings = localStorage.getItem(`voice_settings_${user.id}`);
      if (savedSettings) {
        const settings = JSON.parse(savedSettings);
        setVoiceEnabled(settings.enabled ?? true);
        setVolume(settings.volume ?? 70);
        setRate(settings.rate ?? 90);
        setPitch(settings.pitch ?? 100);
      }
    }
  }, [user]);

  // Salvar configura√ß√µes no localStorage
  const saveSettings = () => {
    if (user) {
      const settings = {
        enabled: voiceEnabled,
        volume,
        rate,
        pitch
      };
      localStorage.setItem(`voice_settings_${user.id}`, JSON.stringify(settings));
    }
  };

  // Aplicar configura√ß√µes em tempo real
  useEffect(() => {
    saveSettings();
  }, [voiceEnabled, volume, rate, pitch]);

  // Testar voz com configura√ß√µes atuais
  const testVoice = () => {
    if (!isSupported || isTestPlaying) return;

    setIsTestPlaying(true);

    const testMessage = `Ol√°, ${user?.name?.split(' ')[0] || 'Doutor'}! Esta √© uma demonstra√ß√£o das configura√ß√µes de voz do Dr. Cannabis IA.`;

    window.speechSynthesis.cancel();

    const utterance = new SpeechSynthesisUtterance(testMessage);
    utterance.volume = volume / 100;
    utterance.rate = rate / 100;
    utterance.pitch = pitch / 100;
    utterance.lang = 'pt-BR';

    // Tentar usar voz em portugu√™s
    const voices = window.speechSynthesis.getVoices();
    const portugueseVoice = voices.find(voice => 
      voice.lang.includes('pt') || voice.lang.includes('BR')
    );

    if (portugueseVoice) {
      utterance.voice = portugueseVoice;
    }

    utterance.onend = () => setIsTestPlaying(false);
    utterance.onerror = () => setIsTestPlaying(false);

    window.speechSynthesis.speak(utterance);
  };

  if (!isSupported) {
    return (
      <Card className="bg-cyber-dark border-gray-700">
        <CardHeader>
          <CardTitle className="text-red-400 flex items-center gap-2">
            <i className="fas fa-exclamation-triangle" />
            Voz n√£o suportada
          </CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-gray-400 text-sm">
            Seu navegador n√£o suporta s√≠ntese de voz (Text-to-Speech). 
            Tente usar um navegador mais recente como Chrome, Edge ou Firefox.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <Card className="bg-cyber-dark border-neon-cyan/30">
        <CardHeader>
          <CardTitle className="text-neon-cyan flex items-center gap-2">
            <i className="fas fa-microphone-alt" />
            Configura√ß√µes de Voz - Dr. Cannabis IA
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">

          {/* Ativar/Desativar Sauda√ß√µes */}
          <div className="flex items-center justify-between">
            <div>
              <h3 className="font-medium text-white">Sauda√ß√µes por Voz</h3>
              <p className="text-sm text-gray-400">
                Dr. Cannabis IA fala quando voc√™ faz login
              </p>
            </div>
            <Switch
              checked={voiceEnabled}
              onCheckedChange={setVoiceEnabled}
              data-testid="voice-enabled-switch"
            />
          </div>

          {voiceEnabled && (
            <>
              {/* Controle de Volume */}
              <div className="space-y-2">
                <div className="flex justify-between">
                  <label className="text-sm font-medium text-white">Volume</label>
                  <span className="text-sm text-gray-400">{volume}%</span>
                </div>
                <Slider
                  value={[volume]}
                  onValueChange={(value) => setVolume(value[0])}
                  max={100}
                  min={0}
                  step={5}
                  className="w-full"
                  data-testid="volume-slider"
                />
              </div>

              {/* Controle de Velocidade */}
              <div className="space-y-2">
                <div className="flex justify-between">
                  <label className="text-sm font-medium text-white">Velocidade</label>
                  <span className="text-sm text-gray-400">{rate}%</span>
                </div>
                <Slider
                  value={[rate]}
                  onValueChange={(value) => setRate(value[0])}
                  max={150}
                  min={50}
                  step={5}
                  className="w-full"
                  data-testid="rate-slider"
                />
              </div>

              {/* Controle de Tom */}
              <div className="space-y-2">
                <div className="flex justify-between">
                  <label className="text-sm font-medium text-white">Tom da Voz</label>
                  <span className="text-sm text-gray-400">{pitch}%</span>
                </div>
                <Slider
                  value={[pitch]}
                  onValueChange={(value) => setPitch(value[0])}
                  max={150}
                  min={50}
                  step={5}
                  className="w-full"
                  data-testid="pitch-slider"
                />
              </div>

              {/* Bot√µes de Teste */}
              <div className="flex gap-3">
                <Button
                  onClick={testVoice}
                  disabled={isTestPlaying}
                  className="flex-1 bg-neon-cyan/20 border border-neon-cyan/50 text-neon-cyan hover:bg-neon-cyan/30"
                  data-testid="test-voice-button"
                >
                  <i className={`fas ${isTestPlaying ? 'fa-volume-up animate-pulse' : 'fa-play'} mr-2`} />
                  {isTestPlaying ? 'Reproduzindo...' : 'Testar Voz'}
                </Button>

                <Button
                  onClick={playGreeting}
                  className="flex-1 bg-green-500/20 border border-green-500/50 text-green-400 hover:bg-green-500/30"
                  data-testid="test-greeting-button"
                >
                  <i className="fas fa-microphone mr-2" />
                  Testar Sauda√ß√£o
                </Button>
              </div>
            </>
          )}

          {/* Preview da Mensagem DESABILITADO - sistema antigo removido */}
          {voiceEnabled && currentMessage && (
            <div className="mt-4">
              {/* <VoiceMessagePreview /> - DESABILITADO */}
              <p className="text-sm text-gray-500">Sistema de sauda√ß√£o antiga desabilitado - Usando apenas Dra. Cannabis IA moderna</p>
            </div>
          )}

        </CardContent>
      </Card>

      {/* Informa√ß√µes sobre o Dr. Cannabis IA */}
      <Card className="bg-cyber-dark border-gray-700">
        <CardContent className="pt-6">
          <div className="flex items-start gap-3">
            <div className="w-12 h-12 bg-gradient-to-br from-neon-cyan to-blue-600 rounded-full flex items-center justify-center flex-shrink-0">
              <i className="fas fa-robot text-white text-lg" />
            </div>
            <div>
              <h3 className="font-medium text-white mb-2">Sobre o Dr. Cannabis IA</h3>
              <p className="text-sm text-gray-400 mb-2">
                O Dr. Cannabis IA √© seu assistente virtual especializado em cannabis medicinal. 
                Ele fornece sauda√ß√µes personalizadas baseadas no hor√°rio e pode ler os resultados das pesquisas.
              </p>
              <div className="text-xs text-gray-500">
                <p>‚Ä¢ Sauda√ß√µes baseadas no hor√°rio (bom dia/tarde/noite)</p>
                <p>‚Ä¢ Personaliza√ß√£o com seu nome</p>
                <p>‚Ä¢ Configura√ß√µes de voz ajust√°veis</p>
                <p>‚Ä¢ Pode ser desabilitado a qualquer momento</p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/hooks/useVoiceGreeting.ts.content:
import { useState, useEffect, useCallback } from 'react';
import { useAuth } from './useAuth';

interface VoiceGreetingConfig {
  enabled: boolean;
  volume: number;
  rate: number;
  pitch: number;
}

export function useVoiceGreeting() {
  const { user, isAuthenticated } = useAuth();
  const [isPlaying, setIsPlaying] = useState(false);
  const [hasPlayedToday, setHasPlayedToday] = useState(false);

  // Configura√ß√£o da voz (DESABILITADA - Sistema modernizado)
  const [config, setConfig] = useState<VoiceGreetingConfig>({
    enabled: false, // DESABILITADO - voz antiga removida
    volume: 0.7,
    rate: 0.9,
    pitch: 1.0
  });

  // Carregar configura√ß√µes do localStorage
  useEffect(() => {
    if (user) {
      const savedSettings = localStorage.getItem(`voice_settings_${user.id}`);
      if (savedSettings) {
        const settings = JSON.parse(savedSettings);
        setConfig({
          enabled: settings.enabled ?? true,
          volume: (settings.volume ?? 70) / 100,
          rate: (settings.rate ?? 90) / 100,
          pitch: (settings.pitch ?? 100) / 100
        });
      }
    }
  }, [user]);

  // Determinar sauda√ß√£o baseada no hor√°rio
  const getGreetingByTime = useCallback(() => {
    const hour = new Date().getHours();

    if (hour >= 5 && hour < 12) {
      return 'Bom dia';
    } else if (hour >= 12 && hour < 18) {
      return 'Boa tarde';
    } else {
      return 'Boa noite';
    }
  }, []);

  // Gerar mensagem personalizada (funciona no modo free)
  const generateGreetingMessage = useCallback(() => {
    const timeGreeting = getGreetingByTime();
    const firstName = user?.name?.split(' ')[0] || 'Visitante';

    // Mensagens para usu√°rios logados
    if (user) {
      const messages = [
        `${timeGreeting}, Dr. ${firstName}! Bem-vindo ao NeuroCann Lab. Estou aqui para auxili√°-lo na an√°lise cient√≠fica de cannabis medicinal.`,
        `${timeGreeting}, ${firstName}! Sou o Dr. Cannabis IA, seu assistente virtual. Como posso ajud√°-lo hoje com suas pesquisas?`,
        `${timeGreeting}! √â um prazer t√™-lo de volta, Dr. ${firstName}. Vamos explorar juntos o universo da cannabis medicinal?`,
        `${timeGreeting}, ${firstName}! Sistema NeuroCann Lab ativo. Pronto para an√°lises cient√≠ficas avan√ßadas.`
      ];
      return messages[Math.floor(Math.random() * messages.length)];
    }

    // Mensagens para modo free
    const freeMessages = [
      `${timeGreeting}! Bem-vindo ao NeuroCann Lab. Sou o Dr. Cannabis IA, explore nosso sistema gratuitamente!`,
      `${timeGreeting}! Descubra o poder da an√°lise cient√≠fica de cannabis medicinal no NeuroCann Lab.`,
      `${timeGreeting}! Sistema NeuroCann Lab ativo. Experimente nossa plataforma de pesquisa m√©dica.`
    ];

    return freeMessages[Math.floor(Math.random() * freeMessages.length)];
  }, [user, getGreetingByTime]);

  // Fun√ß√£o para reproduzir a sauda√ß√£o
  const playGreeting = useCallback(async () => {
    if (!window.speechSynthesis || isPlaying) {
      console.log('üé§ Bloqueado: speechSynthesis ou j√° reproduzindo');
      return;
    }

    // Funciona no modo free tamb√©m
    const userId = user?.id || 'free-user';

    try {
      setIsPlaying(true);
      console.log('üé§ Iniciando reprodu√ß√£o para:', userId);

      // Cancelar qualquer fala anterior
      window.speechSynthesis.cancel();
      await new Promise(resolve => setTimeout(resolve, 100));

      const message = generateGreetingMessage();
      console.log('üé§ Mensagem:', message);
      const utterance = new SpeechSynthesisUtterance(message);

      // Configurar voz
      utterance.volume = config.volume;
      utterance.rate = config.rate;
      utterance.pitch = config.pitch;
      utterance.lang = 'pt-BR';

      // Tentar usar uma voz em portugu√™s
      const voices = window.speechSynthesis.getVoices();
      const portugueseVoice = voices.find(voice => 
        voice.lang.includes('pt') || voice.lang.includes('BR')
      );

      if (portugueseVoice) {
        utterance.voice = portugueseVoice;
      }

      // Eventos da fala
      utterance.onend = () => {
        setIsPlaying(false);
        setHasPlayedToday(true);
        // Marcar no localStorage para n√£o repetir no mesmo dia
        localStorage.setItem(`greeting_played_${userId}`, new Date().toDateString());
        console.log('üé§ Reprodu√ß√£o conclu√≠da');
      };

      utterance.onerror = (error) => {
        setIsPlaying(false);
        console.warn('üé§ Erro na reprodu√ß√£o:', error);
      };

      // Reproduzir
      window.speechSynthesis.speak(utterance);

    } catch (error) {
      console.warn('üé§ Text-to-Speech n√£o suportado:', error);
      setIsPlaying(false);
    }
  }, [user, config, generateGreetingMessage, isPlaying]);

  // Verificar se j√° foi reproduzido hoje (funciona no modo free tamb√©m)
  useEffect(() => {
    const userId = user?.id || 'free-user';
    const lastPlayed = localStorage.getItem(`greeting_played_${userId}`);
    const today = new Date().toDateString();
    setHasPlayedToday(lastPlayed === today);
    console.log('üé§ Status reprodu√ß√£o:', { userId, lastPlayed, today, hasPlayedToday: lastPlayed === today });
  }, [user]);

  // Sistema de sauda√ß√£o autom√°tica TOTALMENTE DESABILITADO
  useEffect(() => {
    console.log('üé§ Sistema de sauda√ß√£o autom√°tica DESABILITADO - voz apenas quando pesquisado');
    // N√ÉO FAZER NADA - usu√°rio n√£o quer voz autom√°tica na entrada
  }, []);

  // Fun√ß√£o para reproduzir manualmente (sempre funciona)
  const playManualGreeting = useCallback(async () => {
    if (!window.speechSynthesis || isPlaying) {
      console.log('üé§ Manual bloqueado: speechSynthesis ou j√° reproduzindo');
      return;
    }

    const userId = user?.id || 'free-user';

    try {
      setIsPlaying(true);
      console.log('üé§ Reprodu√ß√£o manual para:', userId);

      // Cancelar qualquer fala anterior
      window.speechSynthesis.cancel();
      await new Promise(resolve => setTimeout(resolve, 100));

      const message = generateGreetingMessage();
      console.log('üé§ Mensagem manual:', message);
      const utterance = new SpeechSynthesisUtterance(message);

      // Configurar voz
      utterance.volume = config.volume;
      utterance.rate = config.rate;
      utterance.pitch = config.pitch;
      utterance.lang = 'pt-BR';

      // Tentar usar uma voz em portugu√™s
      const voices = window.speechSynthesis.getVoices();
      const portugueseVoice = voices.find(voice => 
        voice.lang.includes('pt') || voice.lang.includes('BR')
      );

      if (portugueseVoice) {
        utterance.voice = portugueseVoice;
      }

      // Eventos da fala
      utterance.onend = () => {
        setIsPlaying(false);
        console.log('üé§ Reprodu√ß√£o manual conclu√≠da');
      };

      utterance.onerror = (error) => {
        setIsPlaying(false);
        console.warn('üé§ Erro na reprodu√ß√£o manual:', error);
      };

      // Reproduzir
      window.speechSynthesis.speak(utterance);

    } catch (error) {
      console.warn('üé§ Erro manual:', error);
      setIsPlaying(false);
    }
  }, [user, config, generateGreetingMessage, isPlaying]);

  // Fun√ß√£o para parar a fala
  const stopGreeting = useCallback(() => {
    if (window.speechSynthesis) {
      window.speechSynthesis.cancel();
      setIsPlaying(false);
    }
  }, []);

  return {
    isPlaying,
    hasPlayedToday,
    playGreeting: playManualGreeting,
    stopGreeting,
    isSupported: !!window.speechSynthesis,
    currentMessage: generateGreetingMessage()
  };
}

