import { useQuery } from '@tanstack/react-query';

interface User {
  id: string;
  email: string;
  name: string;
  role?: string;
  username?: string;
  specialty?: string;
  crm?: string;
}

export function useAuth() {
  const { data: user, isLoading, error } = useQuery({
    queryKey: ['/api/auth/user'],
    retry: false,
  });

  return {
    user: user as User | undefined,
    isLoading,
    isAuthenticated: !!user && !error,
    isAdmin: (user as User)?.role === 'admin'
  };
}

client/src/hooks/useVoiceCommands.ts.content:
import { useState, useEffect, useCallback } from 'react';
import { useAuth } from './useAuth';

interface VoiceCommandsConfig {
  enabled: boolean;
  language: string;
}

// Comandos dispon√≠veis
const VOICE_COMMANDS = {
  search: ['pesquisar', 'procurar', 'buscar', 'encontrar'],
  navigate: {
    scientific: ['estudos cient√≠ficos', 'cient√≠ficos', 'estudos', 'pesquisas'],
    clinical: ['casos cl√≠nicos', 'cl√≠nicos', 'casos'],
    alerts: ['alertas', 'avisos', 'notifica√ß√µes'],
    forum: ['f√≥rum', 'discuss√£o', 'comunidade'],
    profile: ['perfil', 'configura√ß√µes', 'conta'],
    plans: ['planos', 'assinatura', 'upgrade']
  },
  actions: {
    login: ['fazer login', 'entrar', 'conectar'],
    logout: ['sair', 'desconectar', 'logout'],
    help: ['ajuda', 'socorro', 'como usar']
  }
};

export function useVoiceCommands() {
  const { user } = useAuth();
  const [isListening, setIsListening] = useState(false);
  const [isSupported, setIsSupported] = useState(false);
  const [lastCommand, setLastCommand] = useState('');
  const [recognition, setRecognition] = useState<SpeechRecognition | null>(null);

  // Configura√ß√£o padr√£o
  const [config] = useState<VoiceCommandsConfig>({
    enabled: true,
    language: 'pt-BR'
  });

  // Verificar suporte
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      setIsSupported(true);

      const recognitionInstance = new SpeechRecognition();
      recognitionInstance.continuous = false;
      recognitionInstance.interimResults = false;
      recognitionInstance.lang = config.language;

      setRecognition(recognitionInstance);
    }
  }, [config.language]);

  // Processar comando de voz
  const processVoiceCommand = useCallback((transcript: string) => {
    const command = transcript.toLowerCase().trim();
    console.log('üéôÔ∏è Comando recebido:', command);
    setLastCommand(command);

    // SUPER SIMPLES: apenas preencher o campo de pesquisa com o que foi falado
    console.log('üéôÔ∏è Texto falado:', command);

    // Encontrar input de pesquisa
    const searchInput = document.querySelector('input[placeholder*="consulta"]') as HTMLInputElement;

    if (searchInput) {
      // Ativar Dr. AI se necess√°rio
      const drAvatar = document.querySelector('[style*="filter: brightness"]');
      if (drAvatar && !drAvatar.style.filter.includes('brightness(0.75)')) {
        (drAvatar as HTMLElement).click();

        setTimeout(() => {
          searchInput.value = command;
          searchInput.focus();
          searchInput.dispatchEvent(new Event('input', { bubbles: true }));
          speak('Texto inserido no campo de pesquisa');
        }, 500);
      } else {
        // Dr. AI j√° ativo
        searchInput.value = command;
        searchInput.focus();
        searchInput.dispatchEvent(new Event('input', { bubbles: true }));
        speak('Texto inserido no campo de pesquisa');
      }
      return true;
    }

    // Comandos de navega√ß√£o
    for (const [section, keywords] of Object.entries(VOICE_COMMANDS.navigate)) {
      if (keywords.some(keyword => command.includes(keyword))) {
        navigateToSection(section);
        speak(`Navegando para ${section}`);
        return true;
      }
    }

    // Comandos de a√ß√£o
    if (VOICE_COMMANDS.actions.help.some(cmd => command.includes(cmd))) {
      speak('Voc√™ pode me pedir para pesquisar, navegar entre se√ß√µes, ou fazer login. Por exemplo: "pesquisar CBD ansiedade" ou "ir para estudos cient√≠ficos"');
      return true;
    }

    if (VOICE_COMMANDS.actions.login.some(cmd => command.includes(cmd)) && !user) {
      window.location.href = '/api/login';
      speak('Redirecionando para login');
      return true;
    }

    if (VOICE_COMMANDS.actions.logout.some(cmd => command.includes(cmd)) && user) {
      window.location.href = '/api/logout';
      speak('Fazendo logout');
      return true;
    }

    // Comando n√£o reconhecido
    speak('Desculpe, n√£o entendi esse comando. Diga "ajuda" para ver o que posso fazer.');
    return false;
  }, [user]);

  // Fun√ß√£o para navegar
  const navigateToSection = (section: string) => {
    const routes = {
      scientific: '/dashboard',
      clinical: '/dashboard', 
      alerts: '/dashboard',
      forum: '/forum',
      profile: '/profile',
      plans: '/plans'
    };

    const route = routes[section as keyof typeof routes];
    if (route) {
      window.location.href = route;
    }
  };

  // Fun√ß√£o para falar (resposta do Dr. Cannabis IA)
  const speak = (text: string) => {
    if (window.speechSynthesis) {
      const utterance = new SpeechSynthesisUtterance(`Dr. Cannabis IA: ${text}`);
      utterance.lang = 'pt-BR';
      utterance.rate = 0.9;
      utterance.pitch = 1.0;

      const voices = window.speechSynthesis.getVoices();
      const portugueseVoice = voices.find(voice => 
        voice.lang.includes('pt') || voice.lang.includes('BR')
      );

      if (portugueseVoice) {
        utterance.voice = portugueseVoice;
      }

      window.speechSynthesis.speak(utterance);
    }
  };

  // Iniciar escuta
  const startListening = useCallback(() => {
    if (!recognition || !config.enabled || isListening) return;

    setIsListening(true);
    console.log('üéôÔ∏è Iniciando escuta de comandos...');

    recognition.onstart = () => {
      console.log('üéôÔ∏è Escuta iniciada');
    };

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      console.log('üéôÔ∏è Transcript:', transcript);
      processVoiceCommand(transcript);
    };

    recognition.onerror = (event) => {
      console.warn('üéôÔ∏è Erro no reconhecimento:', event.error);
      setIsListening(false);
    };

    recognition.onend = () => {
      console.log('üéôÔ∏è Escuta finalizada');
      setIsListening(false);
    };

    try {
      recognition.start();
    } catch (error) {
      console.warn('üéôÔ∏è Erro ao iniciar:', error);
      setIsListening(false);
    }
  }, [recognition, config.enabled, isListening, processVoiceCommand]);

  // Parar escuta
  const stopListening = useCallback(() => {
    if (recognition && isListening) {
      recognition.stop();
      setIsListening(false);
    }
  }, [recognition, isListening]);

  return {
    isSupported,
    isListening,
    lastCommand,
    startListening,
    stopListening,
    speak
  };
}

client/src/components/ConversationManage.content:
import React, { useState } from 'react';
import { MessageCircle, Plus, Trash2, Merge, FileText, X, Brain } from 'lucide-react';
import { ConversationSynthesis } from './ConversationSynthesis';

interface Conversation {
  id: string;
  title: string;
  messages: Array<{role: 'user' | 'assistant', content: string, timestamp: number}>;
  createdAt: number;
  lastActivity: number;
}

interface ConversationManagerProps {
  currentConversation: Conversation | null;
  conversations: Conversation[];
  onSelectConversation: (conversation: Conversation | null) => void;
  onCreateNew: () => void;
  onDeleteConversation: (id: string) => void;
  onMergeConversations: (ids: string[]) => void;
  onCreateDocument: (conversations: string[]) => void;
  onCreateSynthesis: (synthesis: string, userPrompt: string) => void;
}

export function ConversationManager({
  currentConversation,
  conversations,
  onSelectConversation,
  onCreateNew,
  onDeleteConversation,
  onMergeConversations,
  onCreateDocument,
  onCreateSynthesis
}: ConversationManagerProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedForMerge, setSelectedForMerge] = useState<string[]>([]);
  const [mergeMode, setMergeMode] = useState(false);
  const [showSynthesis, setShowSynthesis] = useState(false);

  const handleMerge = () => {
    if (selectedForMerge.length >= 2) {
      onMergeConversations(selectedForMerge);
      setSelectedForMerge([]);
      setMergeMode(false);
    }
  };

  const handleCreateDocument = () => {
    if (selectedForMerge.length >= 1) {
      onCreateDocument(selectedForMerge);
      setSelectedForMerge([]);
      setMergeMode(false);
    }
  };

  const handleShowSynthesis = () => {
    if (selectedForMerge.length >= 1) {
      setShowSynthesis(true);
    }
  };

  const handleCreateSynthesis = (synthesis: string, userPrompt: string) => {
    onCreateSynthesis(synthesis, userPrompt);
    setSelectedForMerge([]);
    setMergeMode(false);
    setIsOpen(false);
  };

  const toggleSelection = (id: string) => {
    setSelectedForMerge(prev => 
      prev.includes(id) 
        ? prev.filter(x => x !== id)
        : [...prev, id]
    );
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className="fixed top-4 right-4 w-80 max-h-96 bg-black/95 backdrop-blur-xl rounded-xl border border-purple-500/50 z-50 overflow-hidden shadow-2xl">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-purple-500/20">
        <h3 className="text-lg font-semibold text-purple-300">Conversas</h3>
        <div className="flex items-center gap-2">
          {!mergeMode ? (
            <>
              <button
                onClick={onCreateNew}
                className="px-2 py-1 text-green-400 hover:text-green-300 hover:bg-green-400/10 rounded text-xs"
                title="Nova Conversa"
              >
                <Plus className="w-4 h-4" />
              </button>
              <button
                onClick={() => setMergeMode(true)}
                className="px-2 py-1 text-blue-400 hover:text-blue-300 hover:bg-blue-400/10 rounded text-xs"
                title="Mesclar/Exportar Conversas"
              >
                <Merge className="w-4 h-4" />
              </button>
            </>
          ) : (
            <>
              <button
                onClick={handleMerge}
                disabled={selectedForMerge.length < 2}
                className="p-1 text-blue-400 hover:text-blue-300 disabled:opacity-50"
                title="Mesclar Selecionadas"
              >
                <Merge className="w-4 h-4" />
              </button>
              <button
                onClick={handleCreateDocument}
                disabled={selectedForMerge.length === 0}
                className="p-1 text-orange-400 hover:text-orange-300 disabled:opacity-50"
                title="Criar Documento"
              >
                <FileText className="w-4 h-4" />
              </button>
              <button
                onClick={handleShowSynthesis}
                disabled={selectedForMerge.length === 0}
                className="p-1 text-cyan-400 hover:text-cyan-300 disabled:opacity-50"
                title="S√≠ntese Inteligente"
              >
                <Brain className="w-4 h-4" />
              </button>
              <button
                onClick={() => {
                  setMergeMode(false);
                  setSelectedForMerge([]);
                }}
                className="p-1 text-gray-400 hover:text-gray-300"
                title="Cancelar"
              >
                <X className="w-4 h-4" />
              </button>
            </>
          )}
          <button
            onClick={() => setIsOpen(false)}
            className="p-1 text-gray-400 hover:text-gray-300"
          >
            <X className="w-4 h-4" />
          </button>
        </div>
      </div>

      {/* Conversation List */}
      <div className="max-h-80 overflow-y-auto">
        {conversations.map((conv) => (
          <div
            key={conv.id}
            className={`p-3 border-b border-purple-500/10 cursor-pointer transition-all ${
              currentConversation?.id === conv.id 
                ? 'bg-purple-600/20 border-l-4 border-l-purple-400' 
                : 'hover:bg-purple-600/10'
            } ${
              selectedForMerge.includes(conv.id) ? 'bg-blue-600/20' : ''
            }`}
            onClick={() => {
              if (mergeMode) {
                toggleSelection(conv.id);
              } else {
                onSelectConversation(conv);
                setIsOpen(false);
                // For√ßa abertura do card principal minimizado mesmo sem pesquisa ativa
                window.dispatchEvent(new CustomEvent('forceOpenMainCard', { 
                  detail: { conversation: conv } 
                }));
              }
            }}
          >
            <div className="flex items-center justify-between">
              <div className="flex-1 min-w-0">
                <h4 className="text-sm font-medium text-purple-200 truncate">
                  {conv.title}
                </h4>
                <p className="text-xs text-gray-400 mt-1">
                  {conv.messages.length} mensagens ‚Ä¢ {
                    new Date(conv.lastActivity).toLocaleDateString('pt-BR')
                  }
                </p>
              </div>
              {!mergeMode && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onDeleteConversation(conv.id);
                  }}
                  className="p-1 text-red-400 hover:text-red-300 opacity-0 group-hover:opacity-100"
                  title="Excluir"
                >
                  <Trash2 className="w-3 h-3" />
                </button>
              )}
              {mergeMode && (
                <div className={`w-4 h-4 rounded border-2 ${
                  selectedForMerge.includes(conv.id)
                    ? 'bg-blue-400 border-blue-400'
                    : 'border-gray-400'
                }`} />
              )}
            </div>
          </div>
        ))}

        {conversations.length === 0 && (
          <div className="p-6 text-center text-gray-400">
            <MessageCircle className="w-8 h-8 mx-auto mb-2 opacity-50" />
            <p className="text-sm">Nenhuma conversa ainda</p>
            <p className="text-xs text-gray-500 mt-2">
              Fa√ßa uma pergunta ao Dr. Cannabis IA para come√ßar
            </p>
          </div>
        )}
      </div>

      {/* Footer with merge info */}
      {mergeMode && selectedForMerge.length > 0 && (
        <div className="p-3 bg-purple-900/30 border-t border-purple-500/20">
          <p className="text-xs text-purple-300">
            {selectedForMerge.length} conversas selecionadas
          </p>
          <p className="text-xs text-gray-400 mt-1">
            Mesclar: combina em uma nova conversa<br/>
            Documento: cria texto estruturado<br/>
            S√≠ntese: an√°lise inteligente personalizada
          </p>
        </div>
      )}

      {/* Synthesis Modal */}
      {showSynthesis && (
        <ConversationSynthesis
          conversations={conversations}
          selectedConversationIds={selectedForMerge}
          onCreateSynthesis={handleCreateSynthesis}
          onClose={() => setShowSynthesis(false)}
        />
      )}
    </div>
  );
}

client/src/components/ConversationIndica.content:
import { MessageCircle, Eye } from 'lucide-react';

interface Message {
  role: 'user' | 'assistant';
  content: string;
  timestamp: number;
}

interface ConversationIndicatorProps {
  messageCount: number;
  messages: Message[];
  onClear: () => void;
  onToggleHistory: () => void;
  showingHistory: boolean;
  onMinimizeMainCard?: () => void;
}

export function ConversationIndicator({ 
  messageCount, 
  messages, 
  onClear, 
  onToggleHistory,
  showingHistory,
  onMinimizeMainCard
}: ConversationIndicatorProps) {
  if (messageCount === 0) return null;

  return (
    <div className="flex items-center gap-1 mb-1 p-1.5 sm:p-2 sm:gap-2 sm:mb-2 bg-blue-900/20 rounded border border-blue-500/30">
      <MessageCircle className="w-3 h-3 sm:w-4 sm:h-4 text-blue-400" />
      <span className="text-xs text-blue-300">
        <span className="hidden sm:inline">Conversa√ß√£o ativa </span>({Math.floor(messageCount / 2)} trocas)
      </span>
      <button
        onClick={() => {
          if (!showingHistory) {
            // For√ßa abrir o rascunho de estudo
            onToggleHistory(); // Abre o rascunho de estudo
            onMinimizeMainCard?.(); // Minimiza o card principal para mostrar bot√µes
          } else {
            // Se j√° est√° mostrando, volta para pesquisa
            onToggleHistory();
          }
        }}
        className={`text-xs flex items-center gap-1 ${
          showingHistory 
            ? 'text-orange-400 hover:text-orange-300' 
            : 'text-blue-400 hover:text-blue-300'
        }`}
        title={showingHistory ? "Voltar √† pesquisa" : "Abrir Explorar mais e Rascunho"}
      >
        <Eye className="w-2.5 h-2.5 sm:w-3 sm:h-3" />
        {showingHistory ? 'Pesquisar' : 'Ver'}
      </button>
      {/* Bot√£o Limpar - Vers√£o mobile compacta */}
      <button
        onClick={() => {
          onClear();
          onMinimizeMainCard?.(); // Minimiza o card principal automaticamente
        }}
        className="text-xs text-red-400 hover:text-red-300 ml-auto"
        title="Nova conversa"
      >
        <span className="sm:hidden">üóëÔ∏è</span>
        <span className="hidden sm:inline">Nova</span>
      </button>
    </div>
  );
}

