import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Stethoscope, User } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface RoleSelectorProps {
  onRoleSelected: (role: string) => void;
}

export default function RoleSelector({ onRoleSelected }: RoleSelectorProps) {
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  // Verificar se √© admin e redirecionar automaticamente
  useEffect(() => {
    const user = localStorage.getItem('user');
    if (user) {
      const userData = JSON.parse(user);
      if (userData.role === 'admin') {
        window.location.reload();
        return;
      }
    }
  }, []);

  const selectRole = async (role: 'medico' | 'paciente') => {
    setIsLoading(true);

    try {
      // Primeiro verificar se h√° usu√°rio logado
      const user = localStorage.getItem('user');
      if (!user) {
        toast({
          title: "Erro",
          description: "Voc√™ precisa fazer login primeiro.",
          variant: "destructive"
        });
        // Redirecionar para p√°gina de login
        window.location.href = '/login';
        return;
      }

      const response = await fetch('/api/set-role', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ role })
      });

      if (!response.ok) {
        if (response.status === 401) {
          toast({
            title: "Erro de autentica√ß√£o",
            description: "Fa√ßa login novamente.",
            variant: "destructive"
          });
          window.location.href = '/login';
          return;
        }
        throw new Error('Falha ao definir role');
      }

      // Salvar no localStorage para redirecionamento
      const userData = JSON.parse(user);
      localStorage.setItem('user', JSON.stringify({ ...userData, role }));

      toast({
        title: "Perfil definido",
        description: `Redirecionando para o painel ${role === 'medico' ? 'm√©dico' : 'do usu√°rio'}...`
      });

      onRoleSelected(role);
    } catch (error) {
      console.error('Erro ao definir role:', error);
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel definir o perfil. Tente novamente.",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-slate-950 flex items-center justify-center p-4">
      <div className="w-full max-w-4xl space-y-6">
        <div className="text-center space-y-4">
          <h1 className="text-3xl font-bold text-emerald-400">
            Bem-vindo ao NeuroCann Lab
          </h1>
          <p className="text-slate-400">
            Escolha seu perfil para personalizar sua experi√™ncia
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {/* Profissional da Sa√∫de */}
          <Card className="bg-slate-900 border-slate-800 hover:border-emerald-500 transition-colors cursor-pointer">
            <CardHeader>
              <CardTitle className="text-emerald-400 flex items-center justify-center space-x-2">
                <Stethoscope className="h-8 w-8" />
                <span>Profissional da Sa√∫de</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="text-center space-y-2">
                <p className="text-slate-300">
                  Acesso completo a ferramentas m√©dicas, estat√≠sticas de pacientes e recursos profissionais
                </p>

                <div className="flex flex-wrap justify-center gap-2">
                  <Badge variant="outline" className="text-emerald-400 border-emerald-400">
                    Painel M√©dico
                  </Badge>
                  <Badge variant="outline" className="text-emerald-400 border-emerald-400">
                    Consultas IA
                  </Badge>
                  <Badge variant="outline" className="text-emerald-400 border-emerald-400">
                    Relat√≥rios
                  </Badge>
                </div>
              </div>

              <Button
                onClick={() => selectRole('medico')}
                disabled={isLoading}
                className="w-full bg-emerald-500 hover:bg-emerald-600 text-white"
              >
                {isLoading ? 'Configurando...' : 'Sou Profissional da Sa√∫de'}
              </Button>
            </CardContent>
          </Card>

          {/* Usu√°rio/Paciente */}
          <Card className="bg-slate-900 border-slate-800 hover:border-blue-500 transition-colors cursor-pointer">
            <CardHeader>
              <CardTitle className="text-blue-400 flex items-center justify-center space-x-2">
                <User className="h-8 w-8" />
                <span>Usu√°rio</span>
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="text-center space-y-2">
                <p className="text-slate-300">
                  Interface amig√°vel para acompanhamento pessoal, consultas e hist√≥rico m√©dico
                </p>

                <div className="flex flex-wrap justify-center gap-2">
                  <Badge variant="outline" className="text-blue-400 border-blue-400">
                    Painel Pessoal
                  </Badge>
                  <Badge variant="outline" className="text-blue-400 border-blue-400">
                    Dra. Cannabis
                  </Badge>
                  <Badge variant="outline" className="text-blue-400 border-blue-400">
                    Acompanhamento
                  </Badge>
                </div>
              </div>

              <Button
                onClick={() => selectRole('paciente')}
