      } catch (error) {
        console.error(`‚úó Fonte ${source.name}: Erro de conectividade`);
        return false;
      }
    }

    console.log('‚úÖ Todos os dados s√£o de fontes cient√≠ficas verificadas');
    return true;
  }
}

