import { createContext, useContext, useEffect, useState, ReactNode } from 'react';

type Theme = 'night' | 'day';

interface ThemeContextType {
  theme: Theme;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export function ThemeProvider({ children }: { children: ReactNode }) {
  const [theme, setTheme] = useState<Theme>('night');

  useEffect(() => {
    // Load saved theme
    const saved = localStorage.getItem('neurocann-theme') as Theme;
    if (saved) {
      setTheme(saved);
    }
  }, []);

  useEffect(() => {
    // Apply theme to document
    document.documentElement.classList.remove('night', 'day');
    document.documentElement.classList.add(theme);
    localStorage.setItem('neurocann-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'night' ? 'day' : 'night');
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
}

client/src/hooks/useConversations.ts.content:
import { useState, useEffect } from 'react';

interface Message {
  role: 'user' | 'assistant';
  content: string;
  timestamp: number;
}

interface Conversation {
  id: string;
  title: string;
  messages: Message[];
  createdAt: number;
  lastActivity: number;
}

export function useConversations() {
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [currentConversation, setCurrentConversation] = useState<Conversation | null>(null);

  // Load from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem('neurocann-conversations');
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        setConversations(parsed.conversations || []);
        if (parsed.currentId) {
          const current = parsed.conversations?.find((c: Conversation) => c.id === parsed.currentId);
          setCurrentConversation(current || null);
        }
      } catch (error) {
        console.error('Error loading conversations:', error);
      }
    }
  }, []);

  // Save to localStorage whenever conversations change
  useEffect(() => {
    localStorage.setItem('neurocann-conversations', JSON.stringify({
      conversations,
      currentId: currentConversation?.id || null
    }));
  }, [conversations, currentConversation]);

  const createNewConversation = (firstMessage?: string): Conversation => {
    const newConv: Conversation = {
      id: `conv-${Date.now()}`,
      title: firstMessage ? 
        (firstMessage.length > 30 ? firstMessage.substring(0, 30) + '...' : firstMessage) :
        `Nova Conversa ${conversations.length + 1}`,
      messages: [],
      createdAt: Date.now(),
      lastActivity: Date.now()
    };

    setConversations(prev => [newConv, ...prev]);
    setCurrentConversation(newConv);
    return newConv;
  };

  const addMessage = (message: Message, conversationId?: string) => {
    const targetId = conversationId || currentConversation?.id;
    if (!targetId) {
      // Create new conversation if none exists
      const newConv = createNewConversation(message.role === 'user' ? message.content : undefined);
      setConversations(prev => 
        prev.map(c => 
          c.id === newConv.id 
            ? { ...c, messages: [message], lastActivity: Date.now() }
            : c
        )
      );
      return;
    }

    setConversations(prev =>
      prev.map(conv =>
        conv.id === targetId
          ? {
              ...conv,
              messages: [...conv.messages, message],
              lastActivity: Date.now(),
              title: conv.messages.length === 0 && message.role === 'user' 
                ? (message.content.length > 30 ? message.content.substring(0, 30) + '...' : message.content)
                : conv.title
            }
          : conv
      )
    );

    // Update current conversation if it's the target
    if (currentConversation?.id === targetId) {
      setCurrentConversation(prev => 
        prev ? {
          ...prev,
          messages: [...prev.messages, message],
          lastActivity: Date.now()
        } : null
      );
    }
  };

  const deleteConversation = (id: string) => {
    setConversations(prev => prev.filter(c => c.id !== id));
    if (currentConversation?.id === id) {
      setCurrentConversation(null);
    }
  };

  const selectConversation = (conversation: Conversation | null) => {
    setCurrentConversation(conversation);
  };

  const mergeConversations = (ids: string[]) => {
    const toMerge = conversations.filter(c => ids.includes(c.id));
    if (toMerge.length < 2) return;

    // Create merged conversation
    const allMessages = toMerge
      .flatMap(c => c.messages.map(m => ({ ...m, sourceConv: c.title })))
      .sort((a, b) => a.timestamp - b.timestamp);

    const mergedTitle = `Mesclagem: ${toMerge.map(c => c.title).join(' + ')}`;

    const merged: Conversation = {
      id: `merged-${Date.now()}`,
      title: mergedTitle.length > 50 ? mergedTitle.substring(0, 50) + '...' : mergedTitle,
      messages: allMessages,
      createdAt: Date.now(),
      lastActivity: Date.now()
    };

    // Remove original conversations and add merged
    setConversations(prev => [
      merged,
      ...prev.filter(c => !ids.includes(c.id))
    ]);

    setCurrentConversation(merged);
  };

  const createDocument = (conversationIds: string[]) => {
    const sourceConversations = conversations.filter(c => conversationIds.includes(c.id));

    let document = `# Documento Gerado - ${new Date().toLocaleDateString('pt-BR')}\n\n`;

    sourceConversations.forEach(conv => {
      document += `## ${conv.title}\n`;
      document += `*Criado em: ${new Date(conv.createdAt).toLocaleDateString('pt-BR')}*\n\n`;

      conv.messages.forEach(msg => {
        const sender = msg.role === 'user' ? 'üë§ **Usu√°rio**' : 'ü§ñ **Dr. Cannabis IA**';
        document += `${sender}:\n${msg.content}\n\n---\n\n`;
      });

      document += '\n';
    });

    // Create a new conversation with the document
    const docConv: Conversation = {
      id: `doc-${Date.now()}`,
      title: `üìÑ Documento ${new Date().toLocaleDateString('pt-BR')}`,
      messages: [
        {
          role: 'assistant',
          content: document,
          timestamp: Date.now()
        }
      ],
      createdAt: Date.now(),
      lastActivity: Date.now()
    };

    setConversations(prev => [docConv, ...prev]);
    setCurrentConversation(docConv);
  };

  const createSynthesis = (synthesis: string, userPrompt: string) => {
    const synthesisConv: Conversation = {
      id: `synthesis-${Date.now()}`,
      title: `üß† S√≠ntese: ${userPrompt.length > 30 ? userPrompt.substring(0, 30) + '...' : userPrompt}`,
      messages: [
        {
          role: 'user',
          content: userPrompt,
          timestamp: Date.now() - 1000
        },
        {
          role: 'assistant',
          content: synthesis,
          timestamp: Date.now()
        }
      ],
      createdAt: Date.now(),
      lastActivity: Date.now()
    };

    setConversations(prev => [synthesisConv, ...prev]);
    setCurrentConversation(synthesisConv);
  };

  return {
    conversations,
    currentConversation,
    createNewConversation,
    addMessage,
    deleteConversation,
    selectConversation,
    mergeConversations,
    createDocument,
    createSynthesis,
    clearCurrentConversation: () => setCurrentConversation(null)
  };
}

client/src/hooks/useDraCannabisAutoStart.content:
import { useEffect, useState } from 'react';
import { useLocation } from 'wouter';

export function useDraCannabisAutoStart() {
  const [location] = useLocation();
  const [shouldAutoStart, setShouldAutoStart] = useState(false);
  const [hasAutoStarted, setHasAutoStarted] = useState(false);

  useEffect(() => {
    // Auto-iniciar a Dra. Cannabis IA quando usu√°rio entra no dashboard
    if (location === '/dashboard' || location.startsWith('/dashboard/')) {
      const lastAutoStart = localStorage.getItem('draCannabis-lastAutoStart');
      const today = new Date().toDateString();

      // Verificar se j√° fez sauda√ß√£o hoje
      if (lastAutoStart !== today) {
        setShouldAutoStart(true);
        localStorage.setItem('draCannabis-lastAutoStart', today);
      }
    }
  }, [location]);

  const markAutoStarted = () => {
    setHasAutoStarted(true);
    setShouldAutoStart(false);
  };

  return {
    shouldAutoStart,
    hasAutoStarted,
    markAutoStarted
  };
}

client/src/services/professionalAvatarSe.content:
// Sistema profissional de avatar com ElevenLabs integrado ao nosso backend

interface VoiceSettings {
  stability: number;
  similarity_boost: number;
  style?: string;
}

interface SpeakRequest {
  text: string;
  voice_settings?: VoiceSettings;
  use_lip_sync?: boolean;
  driver_image_url?: string;
}

interface SpeakResponse {
  audio_url?: string;
  video_url?: string;
  type: 'audio' | 'video';
}

class ProfessionalAvatarService {
  private currentAudio: HTMLAudioElement | null = null;
  private currentVideo: HTMLVideoElement | null = null;
  private isSpeaking = false;

  // Configura√ß√µes otimizadas para qualidade profissional
  private defaultVoiceSettings: VoiceSettings = {
    stability: 0.75, // Evita voz rob√≥tica
    similarity_boost: 0.9, // Mais fiel √† voz original
    style: 'Conversational' // Estilo natural
  };

  public async speak(
    text: string, 
    options: {
      quality?: 'high' | 'standard';
      useVideo?: boolean;
      voiceSettings?: Partial<VoiceSettings>;
    } = {}
  ): Promise<void> {
    try {
      // Para m√∫ltiplas falas, parar a anterior
      this.stopCurrentSpeech();
      this.isSpeaking = true;

      const requestData: SpeakRequest = {
        text,
        voice_settings: {
          ...this.defaultVoiceSettings,
          ...options.voiceSettings
        },
        use_lip_sync: options.useVideo || false
      };

      console.log('üé≠ Enviando para ElevenLabs + D-ID:', { text: text.substring(0, 50) + '...' });

      // Chamar nosso backend que integra ElevenLabs
      const response = await fetch('/api/avatar/speak', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestData)
      });

      if (!response.ok) {
        const errorData = await response.json();
        if (errorData.type === 'native') {
          console.log('‚ö†Ô∏è Fallback para sistema nativo');
          throw new Error('Sistema nativo deve ser usado');
        }
        throw new Error(`Erro na API: ${response.status}`);
      }

      // Backend retorna √°udio diretamente
      const audioBlob = await response.blob();
      const audioUrl = URL.createObjectURL(audioBlob);
      await this.playAudio(audioUrl);

      console.log('‚úÖ Avatar profissional falou com sucesso');

    } catch (error) {
      console.error('‚ùå Erro no sistema profissional de avatar:', error);
      this.isSpeaking = false;
      throw error;
    }
  }

  private async playAudio(audioUrl: string): Promise<void> {
    return new Promise((resolve, reject) => {
      this.currentAudio = new Audio(audioUrl);

      this.currentAudio.onloadeddata = () => {
        console.log('üîä √Åudio ElevenLabs carregado, reproduzindo...');
      };

      this.currentAudio.onended = () => {
        this.isSpeaking = false;
        console.log('‚úÖ √Åudio ElevenLabs terminou');
        resolve();
      };

      this.currentAudio.onerror = (error) => {
        this.isSpeaking = false;
        console.error('‚ùå Erro na reprodu√ß√£o do √°udio:', error);
        reject(error);
      };

      this.currentAudio.play()
        .catch(error => {
          this.isSpeaking = false;
          reject(error);
        });
    });
  }

  private async playVideo(videoUrl: string): Promise<void> {
    return new Promise((resolve, reject) => {
      // Criar elemento de v√≠deo para avatar com lip-sync
      this.currentVideo = document.createElement('video');
      this.currentVideo.src = videoUrl;
      this.currentVideo.autoplay = true;
      this.currentVideo.muted = false; // √Åudio do D-ID

      // Substituir avatar est√°tico temporariamente
      const avatarContainer = document.querySelector('[data-avatar-container]');
      if (avatarContainer) {
        avatarContainer.appendChild(this.currentVideo);
      }

      this.currentVideo.onloadeddata = () => {
        console.log('üé¨ V√≠deo D-ID + ElevenLabs carregado, reproduzindo...');
      };

      this.currentVideo.onended = () => {
        this.isSpeaking = false;
        if (avatarContainer && this.currentVideo) {
          avatarContainer.removeChild(this.currentVideo);
        }
        console.log('‚úÖ V√≠deo D-ID terminou');
        resolve();
      };

      this.currentVideo.onerror = (error) => {
        this.isSpeaking = false;
        console.error('‚ùå Erro na reprodu√ß√£o do v√≠deo:', error);
        reject(error);
      };
    });
  }

  public stopCurrentSpeech(): void {
    if (this.currentAudio) {
      this.currentAudio.pause();
      this.currentAudio = null;
    }

    if (this.currentVideo) {
      this.currentVideo.pause();
      const avatarContainer = document.querySelector('[data-avatar-container]');
      if (avatarContainer && avatarContainer.contains(this.currentVideo)) {
        avatarContainer.removeChild(this.currentVideo);
      }
      this.currentVideo = null;
    }

    this.isSpeaking = false;
  }

  public get isCurrentlySpeaking(): boolean {
    return this.isSpeaking;
  }

  // M√©todo para sauda√ß√£o com m√°xima qualidade
  public async speakWelcome(): Promise<void> {
    const welcomeText = "Ol√°! Bem-vindo ao NeuroCann Lab! Eu sou a Dra. Cannabis IA, sua assistente m√©dica especializada em cannabis medicinal. Como posso ajud√°-lo hoje?";

    await this.speak(welcomeText, {
      quality: 'high',
      useVideo: true, // Usar lip-sync para sauda√ß√£o
      voiceSettings: {
        stability: 0.8, // M√°xima estabilidade para sauda√ß√£o
        similarity_boost: 1.0 // M√°xima fidelidade
      }
    });
  }

  // M√©todo para consultas m√©dicas com qualidade padr√£o
  public async speakMedicalResponse(text: string): Promise<void> {
    await this.speak(text, {
      quality: 'standard',
      useVideo: false, // Apenas √°udio para respostas r√°pidas
      voiceSettings: {
        stability: 0.75,
        similarity_boost: 0.85
      }
    });
  }
}

export const professionalAvatarService = new ProfessionalAvatarService();

